import React, { useState } from 'react';
import { Box } from '@mui/material';
import { Typography } from '@mui/joy';
import CardBasked from './CardBasked';
import Button from '@mui/material/Button';

export default function SaleBasket({ setIsModalOpen }) {
  const [cartItems, setCartItems] = useState(
    JSON.parse(localStorage.getItem('cartItems')) || []
  );

  const handleClearCart = () => {
    localStorage.clear();
    setCartItems([]);
  };

  return (
    <Box
      display={'flex'}
      flexDirection={'column'}
      sx={{
        backgroundColor: 'white',
        position: 'absolute',
      }}
      borderRadius={'8px'}
      padding={'10px'}
      paddingTop={'20px'}
      width={'35%'}
    >
      <Typography level='h4' textAlign={'center'} marginBottom={'20px'}>
        Ваш заказ
      </Typography>
      <Box display={'flex'} flexDirection={'row'} gap={'30px'}>
        <CardBasked cartItems={cartItems} setCartItems={setCartItems} />
        <Box
          gap={'10px'}

          display={'flex'}
          flexDirection={'column'}
          justifyContent={'center'}
        >
          <Button
            variant='contained'
            color='success'
            sx={{ height: '40px', lineHeight: '1' }}
            onClick={() => setIsModalOpen(true)}
          >
            Оформить заказ
          </Button>

          <Button
            variant='outlined'
            color='error'
            sx={{ height: '40px', lineHeight: '1' }}
            onClick={handleClearCart}
          >
            Очистить корзину
          </Button>
        </Box>
      </Box>
    </Box>
  );
}
