import * as React from 'react';
import Button from '@mui/joy/Button';
import Divider from '@mui/joy/Divider';
import DialogTitle from '@mui/joy/DialogTitle';
import DialogContent from '@mui/joy/DialogContent';
import DialogActions from '@mui/joy/DialogActions';
import Modal from '@mui/joy/Modal';
import ModalDialog from '@mui/joy/ModalDialog';
import DeleteForever from '@mui/icons-material/DeleteForever';
import WarningRoundedIcon from '@mui/icons-material/WarningRounded';

export default function DeleteAlert({ card, onDelete }) {
  const [open, setOpen] = React.useState(false);

  const handleDelete = () => {
    const data = JSON.parse(localStorage.getItem('cartItems')) || [];

    const index = data.findIndex((item) => item.id === card.id);

    data.splice(index, 1);

    localStorage.setItem('cartItems', JSON.stringify(data));

    onDelete();
    setOpen(false);
  };

  return (
    <React.Fragment>
      <Button
        variant="solid"
        color="danger"
        endDecorator={<DeleteForever sx={{buttonGap: '0px'}} />}
        onClick={() => setOpen(true)}
        sx={{position: 'absolute', top: '5px', left: '5px' }}
      >

      </Button>
      <Modal open={open} onClose={() => setOpen(false)}>
        <ModalDialog variant="outlined" role="alertdialog">
          <DialogTitle sx={{alignItems: 'center'}}>
            <WarningRoundedIcon />
            Подтверждение
          </DialogTitle>
          <Divider />
          <DialogContent>
              Вы уверены что хотите удалить товар?
            </DialogContent>
          <DialogActions>
            <Button variant="solid" color="danger" onClick={() => setOpen(false)}>
              Нет
            </Button>
            <Button variant="plain" color="success" onClick={handleDelete}>
              Да
            </Button>
          </DialogActions>
        </ModalDialog>
      </Modal>
    </React.Fragment>
  );
}
