import React, { useState } from 'react';
import Radio from '@mui/joy/Radio';
import RadioGroup from '@mui/joy/RadioGroup';
import { Box, Typography } from '@mui/joy';

const radioGroups = [
  {
    id: 'height',
    label: 'Высота:',
    options: ['19 см', '220 см', '250 см', '270 см'],
  },
  {
    id: 'diameter',
    label: 'Диаметр:',
    options: ['135 см', '143 см', '155 см', '156 см'],
  },
  {
    id: 'fastenings',
    label: 'Крепления:',
    options: ['Отгибные'],
    defaultValue: 'Отгибные',
  },
];

export default function RadioModal() {
  const [values, setValues] = useState({});
  const handleChange = (groupId, value) => {
    setValues((prevValues) => ({ ...prevValues, [groupId]: value }));
  };

  return (
    <Box display="flex" flexDirection="column" gap={2} textAlign={'center'} alignItems={'center'}>
      {radioGroups.map(({ id, label, options, defaultValue }) => (
        <Box key={id} sx={{ display: 'flex', flexDirection: 'column', width: 'fit-content' }}>
          <Typography fontWeight="lg" fontSize="sm">
            {label}
          </Typography>
          <RadioGroup
            orientation="horizontal"
            aria-labelledby={id}
            name={id}
            value={values[id] || defaultValue || ''}
            onChange={(event) => handleChange(id, event.target.value)}
            sx={{
              minHeight: 30,
              padding: '4px',
              borderRadius: '12px',
              bgcolor: 'neutral.softBg',
              '--RadioGroup-gap': '4px',
              '--Radio-actionRadius': '8px',
            }}
          >
            {options.map((option) => (
              <Radio
                key={option}
                color="neutral"
                value={option}
                disableIcon
                label={option}
                variant="plain"
                sx={{
                  px: 1,
                  alignItems: 'center',
                }}
                slotProps={{
                  action: ({ checked }) => ({
                    sx: {
                      ...(checked && {
                        bgcolor: 'background.surface',
                        boxShadow: 'sm',
                        '&:hover': {
                          bgcolor: 'background.surface',
                        },
                      }),
                    },
                  }),
                }}
              />
            ))}
          </RadioGroup>
        </Box>
      ))}
    </Box>
  );
}
