import React, { useState } from 'react';
import { Box } from '@mui/joy';
import Header from './Header';
import SelectCat from './SelectCat';
import SelectHeight from './SelectHeight';
import SelectRad from './SelectRad';
import SelectBranch from './SelectBranch';
import AspectRatio from '@mui/joy/AspectRatio';
import Button from '@mui/joy/Button';
import Card from '@mui/joy/Card';
import CardContent from '@mui/joy/CardContent';
import Typography from '@mui/joy/Typography';
import CustomIcons from './Pagination';
import Footer from './Footer';
import SelectBtn from './SelectBtn';
import { cardData } from '../constans/catalog';
import ModalItem from './ModalItem';

export default function FormControlLabelPosition() {
  const [selectedCat, setSelectedCat] = useState('');
  const [selectedHeight, setSelectedHeight] = useState('');
  const [selectedRad, setSelectedRad] = useState('');
  const [selectedBranch, setSelectedBranch] = useState('');
  const [selectedValues, setSelectedValues] = useState({});
  const [cards, setCards] = useState(cardData);
  const [cartItems, setCartItems] = useState([]);

  const searchButton = () => {
    const filterValues = selectedValues;
    const filteredData = cardData.filter((obj) => {
      // Фильтр по креплению
      if (
        filterValues.branch &&
        filterValues.branch.length > 0 &&
        !filterValues.branch.includes(obj.branch)
      ) {
        return false;
      }

      // Фильтр по радиусу
      const radius = parseInt(obj.radius);
      if (
        filterValues.radius &&
        ((filterValues.radius.min && filterValues.radius.min > radius) ||
          (filterValues.radius.max && filterValues.radius.max < radius))
      ) {
        return false;
      }

      // Фильтр по категориям
      if (
        filterValues.cat &&
        filterValues.cat.length > 0 &&
        !filterValues.cat.includes(obj.type)
      ) {
        return false;
      }

      // Фильтр по высоте
      const height = parseInt(obj.height);
      if (
        (filterValues.height && filterValues.height.min > height) ||
        (filterValues.height &&
          filterValues.height.max &&
          filterValues.height.max < height)
      ) {
        return false;
      }

      return true;
    });

    setCards(filteredData);
  };

  const handleAddToCart = (product) => {
    const index = cartItems.findIndex((item) => item.id === product.id);
    if (index !== -1) {
      const updatedCartItems = [...cartItems];
      updatedCartItems[index].quantity += 1;
      updatedCartItems[index].totalPrice += product.price;
      setCartItems(updatedCartItems);
      localStorage.setItem('cartItems', JSON.stringify(updatedCartItems)); // сохраняем обновленный массив в localStorage
    } else {
      const newCartItem = {
        id: product.id,
        name: product.name,
        price: product.price,
        imageSrc: product.imageSrc,
        title: product.title,
        quantity: 1,
        totalPrice: product.price,
      };
      setCartItems([...cartItems, newCartItem]);
      localStorage.setItem('cartItems', JSON.stringify(updatedCartItems));
    }
  };

  const handleReset = () => {
    window.location.reload();
  };

  const handleCatChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      cat: event.target.value,
    }));
  };

  const handleHeightChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      height: event.target.value,
    }));
  };

  const handleRadChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      radius: event.target.value,
    }));
  };

  const handleBranchChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      branch: event.target.value,
    }));
  };

  return (
    <Box
      display={'flex'}
      flexDirection={'column'}
      alignItems={'center'}
      marginBottom={'30px'}
    >
      <Header />
      <Box
        display={'flex'}
        flexDirection={'row'}
        marginTop={'100px'}
        gap={'30px'}
        alignItems={'center'}
        flexWrap={'wrap'}
      >
        <SelectCat
          selectedCat={selectedCat}
          setSelectedCat={setSelectedCat}
          handleCatChange={handleCatChange}
        />
        <SelectHeight
          selectedHeight={selectedHeight}
          setSelectedHeight={setSelectedHeight}
          handleHeightChange={handleHeightChange}
        />
        <SelectRad
          selectedRad={selectedRad}
          setSelectedRad={setSelectedRad}
          handleRadChange={handleRadChange}
        />
        <SelectBranch
          setSelectedBranch={setSelectedBranch}
          selectedBranch={selectedBranch}
          handleBranchChange={handleBranchChange}
        />
        <SelectBtn onReset={handleReset} onSearch={searchButton} />
      </Box>
      <Box
        display={'flex'}
        flexWrap={'wrap'}
        gap={'30px'}
        justifyContent={'center'}
        marginTop={'30px'}
      >
        {cards.map((card, index) => (
          <Card key={index} sx={{ width: 200 }}>
            <div>
              <Typography level='title-lg'>{card.title}</Typography>
            </div>
            <AspectRatio minHeight='320px' maxHeight='200px'>
              <ModalItem />
            </AspectRatio>
            <CardContent orientation='horizontal'>
              <div>
                <Typography level='body-xs'>Цена:</Typography>
                <Typography fontSize='lg' fontWeight='lg'>
                  {card.price}
                </Typography>
              </div>
              <Button
                variant='solid'
                size='md'
                color='primary'
                aria-label={`Explore ${card.title}`}
                sx={{ ml: 'auto', alignSelf: 'center', fontWeight: 600 }}
                onClick={() => handleAddToCart(card)}
              >
                В корзину
              </Button>
            </CardContent>
          </Card>
        ))}
      </Box>
      <CustomIcons sx={{ marginTop: '30px' }} />
      <Footer />
    </Box>
  );
}
