import React, { useState } from 'react';
import { FormControl } from '@mui/material';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';

const optionsRadius = [
  { id: '1', label: 'До 100см' },
  { id: '2', label: 'От 100см до 120см' },
  { id: '3', label: 'От 120см до 140см' },
  { id: '4', label: '150 см' },
  { id: '5', label: '100 см' },
  { id: '6', label: '40 см' },
];

export default function SelectRad({ selectedRad, setSelectedRad }) {
  const handleChangeRadius = (event) => {
    setSelectedRad(event.target.value);
  };

  return (
    <FormControl sx={{ m: 1, minWidth: 120 }} size='small'>
      <InputLabel id='demo-select-small-label'>Диаметр</InputLabel>
      <Select
        labelId='demo-select-small-label'
        id='demo-select-small'
        value={selectedRad}
        label='Age'
        onChange={handleChangeRadius}
        sx={{ width: '120px' }}
      >
        {optionsRadius.map(({ id, label }) => (
          <MenuItem key={id} value={id}>
            {label}
          </MenuItem>
        ))}
      </Select>
    </FormControl>
  );
}
