import React, { useState } from 'react';
import { FormControl } from '@mui/material';

import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';

const optionsCat = [
  { id: '1', label: 'Литые елки' },
  { id: '2', label: 'Уличные елки' },
  { id: '3', label: 'Елки с лампочками' },
  { id: '4', label: 'Заснежные елки' },
  { id: '5', label: 'Комбинированные елки' },
  { id: '6', label: 'Настольные елки' },
];

export default function SelectCat({ selectedCat, setSelectedCat, handleCatChange }) {
  const [categoty, setCategoty] = useState('');

  const handleChangeCat = (event) => {
    setCategoty(event.target.value);
    handleCatChange(event); 
  };
  return (
    <FormControl sx={{ m: 1, minWidth: 120 }} size='small'>
      <InputLabel id='demo-select-small-label'>Каталог</InputLabel>
      <Select
        labelId='demo-select-small-label'
        id='demo-select-small'
        value={categoty}
        label='Age'
        onChange={handleChangeCat}
        sx={{ width: '130px' }}
      >
        {optionsCat.map(({ id, label }) => (
          <MenuItem key={id} value={categoty}>
            {categoty}
          </MenuItem>
        ))}
      </Select>
    </FormControl>
  );
}
