import React, { useState } from 'react';
import { Box } from '@mui/joy';
import Header from './Header';
import SelectCat from './SelectCat';
import SelectHeight from './SelectHeight';
import SelectRad from './SelectRad';
import SelectBranch from './SelectBranch';
import AspectRatio from '@mui/joy/AspectRatio';
import Button from '@mui/joy/Button';
import Card from '@mui/joy/Card';
import CardContent from '@mui/joy/CardContent';
import Typography from '@mui/joy/Typography';
import CustomIcons from './Pagination';
import Footer from './Footer';
import SelectBtn from './SelectBtn';
import { cardData } from '../constans/catalog';

export default function FormControlLabelPosition() {
  const [selectedCat, setSelectedCat] = useState('');
  const [selectedHeight, setSelectedHeight] = useState('');
  const [selectedRad, setSelectedRad] = useState('');
  const [selectedBranch, setSelectedBranch] = useState('');
  const [selectedValues, setSelectedValues] = useState({});
  const [cards, setCards] = useState(cardData);

  const treePatam = () => {
    const filterValues = selectedValues;
    const cardType = cardData.map((item) => item.type);

    const cardRadius = cardData.map((item) => item.radius);
    const cardBranch =cardData.map((item) => item.branch);
    const selectType = cardType.filter((type) => filterValues.cat);

    const selectRadius = cardRadius.filter((radius) => filterValues.radius);
    const selectBranch = cardBranch.filter((branch) => filterValues.branch);


  }
  // treePatam()



  const searchButton = () => {
    const filterValues = treeParams(); // получаем значения фильтров из treeParams
    const filteredData = cardData.filter((obj) => {
      const height = parseInt(obj.height);
      const type = obj.type;
      const radius = obj.radius;
      const branch = obj.branch;
      // применяем фильтры к данным
      return (
        height >= filterValues.height.min &&
        height <= filterValues.height.max &&
        filterValues.cat.includes(type) &&
        filterValues.radius.includes(radius) &&
        filterValues.branch.includes(branch)
      );
    });
    setCards(filteredData);
  };

  const handleReset = () => {
    setSelectedCat('');
    setSelectedHeight('');
    setSelectedRad('');
    setSelectedBranch('');
  };

  const handleCatChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      cat: event.target.value,
    }));
  };

  const handleHeightChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      height: event.target.value,
    }));
  };

  const handleRadChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      radius: event.target.value,
    }));
  };

  const handleBranchChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      branch: event.target.value,
    }));
  };

  return (
    <Box
      display={'flex'}
      flexDirection={'column'}
      alignItems={'center'}
      marginBottom={'30px'}
    >
      <Header />
      <Box
        display={'flex'}
        flexDirection={'row'}
        marginTop={'100px'}
        gap={'30px'}
        alignItems={'center'}
        flexWrap={'wrap'}
      >
        <SelectCat
          selectedCat={selectedCat}
          setSelectedCat={setSelectedCat}
          handleCatChange={handleCatChange}
        />
        <SelectHeight
          selectedHeight={selectedHeight}
          setSelectedHeight={setSelectedHeight}
          handleHeightChange={handleHeightChange}
        />
        <SelectRad
          selectedRad={selectedRad}
          setSelectedRad={setSelectedRad}
          handleRadChange={handleRadChange}
        />
        <SelectBranch
          setSelectedBranch={setSelectedBranch}
          selectedBranch={selectedBranch}
          handleBranchChange={handleBranchChange}
        />
        <SelectBtn onReset={handleReset} onSearch={searchButton} />
      </Box>
      <Box
        display={'flex'}
        flexWrap={'wrap'}
        gap={'30px'}
        justifyContent={'center'}
        marginTop={'30px'}
      >
        {cards.map((card, index) => (
          <Card key={index} sx={{ width: 200 }}>
            <div>
              <Typography level='title-lg'>{card.title}</Typography>
            </div>
            <AspectRatio minHeight='320px' maxHeight='200px'>
              <img
                src={card.imageSrc}
                srcSet={card.imageSrcSet}
                loading='lazy'
                alt=''
              />
            </AspectRatio>
            <CardContent orientation='horizontal'>
              <div>
                <Typography level='body-xs'>Цена:</Typography>
                <Typography fontSize='lg' fontWeight='lg'>
                  {card.price}
                </Typography>
              </div>
              <Button
                variant='solid'
                size='md'
                color='primary'
                aria-label={`Explore ${card.title}`}
                sx={{ ml: 'auto', alignSelf: 'center', fontWeight: 600 }}
              >
                В корзину
              </Button>
            </CardContent>
          </Card>
        ))}
      </Box>
      <CustomIcons sx={{ marginTop: '30px' }} />
      <Footer />
    </Box>
  );
}
