import React from 'react';
import AspectRatio from '@mui/joy/AspectRatio';
import Card from '@mui/joy/Card';
import CardContent from '@mui/joy/CardContent';
import CardOverflow from '@mui/joy/CardOverflow';
import Divider from '@mui/joy/Divider';
import Typography from '@mui/joy/Typography';

import Link from '@mui/joy/Link';

export default function CardBasked() {
  const cartItems = JSON.parse(localStorage.getItem('cartItems')) || [];

  return (
    <div>
      {/* Отображаем каждый товар в корзине */}
      {cartItems.map((product) => (
        <Card key={index} variant='outlined' sx={{ maxWidth: 220 }}>
          <CardOverflow>
            <AspectRatio ratio='2'>
              <img src={item.imageSrc} alt={item.title} />
            </AspectRatio>
          </CardOverflow>
          <CardContent>
            <Typography level='title-md'>
              <Link href='#multiple-actions' overlay underline='none'>
                {<div>
      {/* Отображаем каждый товар в корзине */}
      {cartItems.map((item, index) => (
        <Card key={index} variant='outlined' sx={{ maxWidth: 220 }}>
          <CardMedia>
            <AspectRatio ratio='2'>
              <img src={item.imageSrc} alt={item.title} />
            </AspectRatio>
          </CardMedia>
          <CardContent>
            <Typography variant='h6'>
              <Link href='#multiple-actions' underline='none'>
                {item.title}
              </Link>
            </Typography>
            <Typography variant='body1'>
              <Link href='#multiple-actions'>{item.price}</Link>
            </Typography>
          </CardContent>
          <CardActions>
            <Divider orientation='vertical' flexItem />
            <Typography variant='body1'>1 шт</Typography>
            <Divider orientation='vertical' flexItem />
            <Typography variant='body1'>Итог: {item.price}</Typography>
          </CardActions>
        </Card>
      ))}
    </div>.title}
              </Link>
            </Typography>
            <Typography level='body-sm'>
              <Link href='#multiple-actions'>{item.price}</Link>
            </Typography>
          </CardContent>
          <CardOverflow variant='soft'>
            <Divider inset='context' />
            <CardContent orientation='horizontal'>
              <Typography level='body-xs'>1 шт</Typography>
              <Divider orientation='vertical' />
              <Typography level='body-xs'>Итог: {item.price}</Typography>
            </CardContent>
          </CardOverflow>
        </Card>
      ))}
    </div>
  );
}
