import React, { useState } from 'react';
import { Box, Typography } from '@mui/joy';
import Button from '@mui/joy/Button';
import RadioModal from './RadioModal';
import CloseIcon from '@mui/icons-material/Close';
import ModalItem from './ModalItem';

export default function CardItem({ isOpen, onClose, card, addBasket }) {
  const [price, setPrice] = useState(card.price);
  const [initialPrice, setInitialPrice] = useState(card.price);

  const handlePriceChange = (newPrice) => {
    setPrice(newPrice);
  };

  return (
    <>
      {isOpen && (
        <>
          <div
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              backgroundColor: 'rgba(0, 0, 0, 0.5)',
              zIndex: 1,
            }}
            onClick={onClose}
          />
          <div
            style={{
              display: 'flex',
              backgroundColor: 'white',
              padding: '20px',
              borderRadius: '30px',
              gap: '10px',
              position: 'fixed',
              zIndex: 2,
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
            }}
          >
            <CloseIcon
              fontSize='large'
              sx={{
                position: 'absolute',
                top: '-25px',
                right: '-25px',
                cursor: 'pointer',
              }}
              onClick={onClose}
            />
            <Box sx={{ width: '250px' }}>
              <ModalItem images={card.imageSrc.map((image) => image.image)} />
              <Typography level='h4' variant='string' color='neutral'>
                Цена: {price}
              </Typography>
            </Box>
            <Box
              display={'flex'}
              flexDirection={'column'}
              alignItems={'center'}
            >
              <Typography
                level='h3'
                variant='string'
                color='neutral'
                textAlign={'center'}
                marginBottom={'15px'}
              >
                {card.title}
              </Typography>
              <RadioModal
                card={card}
                onPriceChange={handlePriceChange}
                initialPrice={initialPrice}
              />

              <Button
                variant='outlined'
                style={{ margin: 'auto 25%', width: '150px' }}
                onClick={() => addBasket(card)}
              >
                В корзину
              </Button>
            </Box>
          </div>
        </>
      )}
    </>
  );
}
