import React, {useState} from 'react';
import { FormControl } from '@mui/material';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';

const optionsHeight = [
  { id: '100', label: '100 см' },
  { id: '110', label: '110 см' },
  { id: '130', label: '130 см' },
  { id: '140', label: '140 см' },
  { id: '150', label: '150 см' },
  { id: '160', label: '160 см' },
  { id: '170', label: '170 см' },
  { id: '180', label: '180 см' },
  { id: '200', label: '200 см' },
  { id: '210', label: '210 см' },
  { id: '310', label: '310 см' },
];

export default function SelectHeight({ selectedHeight, setSelectedHeight }) {
  const [height, setHeight] = React.useState('');

  const handleHeightChange = (event) => {
    setHeight(event.target.value);
    filterDataByHeight(event.target.value);
  };


  React.useEffect(() => {
    setSelectedHeight(height);
  }, [height, setSelectedHeight]);

  return (
    <FormControl sx={{ m: 1, minWidth: 120 }} size='small'>
      <InputLabel id='demo-select-small-label'>Высота</InputLabel>
      <Select
        labelId='demo-select-small-label'
        id='demo-select-small'
        value={selectedHeight}
        label='Age'
        onChange={handleHeightChange}
        sx={{ width: '120px' }}
      >
        {optionsHeight.map(({ id, label }) => (
          <MenuItem key={id} value={id}>
            {label}
          </MenuItem>
        ))}
      </Select>
    </FormControl>
  );
}
