import React, { useState } from 'react';
import { Box } from '@mui/joy';
import Header from './Header';
import SelectCat from './SelectCat';
import SelectHeight from './SelectHeight';
import SelectRad from './SelectRad';
import SelectBranch from './SelectBranch';
import BasicCard from './Cards';
import CustomIcons from './Pagination';
import Footer from './Footer';
import SelectBtn from './SelectBtn';
import { cardData } from '../constans/catalog';

export default function FormControlLabelPosition() {
  const [selectedCat, setSelectedCat] = useState('');
  const [selectedHeight, setSelectedHeight] = useState('');
  const [selectedRad, setSelectedRad] = useState('');
  const [selectedBranch, setSelectedBranch] = useState('');


  const filterDataByHeight = (height) => {
    if (height) {
      const filtered = cardData.filter((card) => card.height === height);
      setFilteredData(filtered);
    } else {
      setFilteredData(cardData);
    }
  };

  const filteredData = React.useMemo(() => {
    if (selectedHeight) {
      return cardData.filter((card) => card.height === selectedHeight);
    }
    return cardData;
  }, [selectedHeight]);

  const handleReset = () => {
    setSelectedCat('');
    setSelectedHeight('');
    setSelectedRad('');
    setSelectedBranch('');
  };

  const handleCatChange = (event) => {
    setSelectedCat(event.target.value);
  };

  const handleBranchChange = (event) => {
    setSelectedBranch(event.target.value);
  };

  return (
    <Box
      display={'flex'}
      flexDirection={'column'}
      alignItems={'center'}
      marginBottom={'30px'}
    >
      <Header />
      <Box
        display={'flex'}
        flexDirection={'row'}
        marginTop={'100px'}
        gap={'30px'}
        alignItems={'center'}
        flexWrap={'wrap'}
      >
        <SelectCat
          selectedCat={selectedCat}
          handleCatChange={handleCatChange}
        />
         <SelectHeight
        selectedHeight={selectedHeight}
        setSelectedHeight={setSelectedHeight}
      />
        <SelectRad selectedRad={selectedRad} setSelectedRad={setSelectedRad} />
        <SelectBranch
          selectedBranch={selectedBranch}
          handleBranchChange={handleBranchChange}
        />
        <SelectBtn onReset={handleReset} />
      </Box>
      <Box
        display={'flex'}
        flexWrap={'wrap'}
        gap={'30px'}
        justifyContent={'center'}
        marginTop={'30px'}
      >
              <BasicCard filteredData={filteredData} />

      </Box>
      <CustomIcons sx={{ marginTop: '30px' }} />
      <Footer />
    </Box>
  );
}
