import React, {useState} from 'react'
import Radio from '@mui/joy/Radio';
import RadioGroup from '@mui/joy/RadioGroup';
import { Box } from '@mui/joy';

export default function RadioModal() {
  const [height, setHeight] = useState('');
  const [diameter, setDiameter] = useState('');
  const [fastenings, setFastenings] = useState('');
  return (
    <>
     <Box display={'flex'} flexDirection={'column'} gap={'15px'}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: '5px', flexDirection: 'column' }}>
            <Typography
              id='segmented-controls-example'
              fontWeight='lg'
              fontSize='sm'
            >
              Высота:
            </Typography>
            <RadioGroup
              orientation='horizontal'
              aria-labelledby='segmented-controls-example'
              name='height' 
              value={height}
              onChange={(event) => setHeight(event.target.value)}
              sx={{
                minHeight: 30,
                padding: '4px',
                borderRadius: '8px',
                bgcolor: 'neutral.softBg',
                '--RadioGroup-gap': '4px',
                '--Radio-actionRadius': '8px',
              }}
            >
              {['190 см', '220 см', '250 см', '270 см'].map((item) => (
                <Radio
                  key={item}
                  color='neutral'
                  value={item}
                  disableIcon
                  label={item}
                  variant='plain'
                  sx={{
                    px: 1,
                    alignItems: 'center',
                  }}
                  slotProps={{
                    action: ({ checked }) => ({
                      sx: {
                        ...(checked && {
                          bgcolor: 'background.surface',
                          boxShadow: 'sm',
                          '&:hover': {
                            bgcolor: 'background.surface',
                          },
                        }),
                      },
                    }),
                  }}
                />
              ))}
            </RadioGroup>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: '5px', flexDirection: 'column'}}>
            <Typography id='segmented-control' fontWeight='lg' fontSize='sm'>
              Диаметр:
            </Typography>
            <RadioGroup
              orientation='horizontal'
              aria-labelledby='segmented-control'
              name='diameter' 
              value={diameter}
              onChange={(event) => setDiameter(event.target.value)}
              sx={{
                minHeight: 30,
                padding: '4px',
                borderRadius: '12px',
                bgcolor: 'neutral.softBg',
                '--RadioGroup-gap': '4px',
                '--Radio-actionRadius': '8px',
              }}
            >
              {['135 см', '143 см', '155 см', '156 см'].map((item) => (
                <Radio
                  key={item}
                  color='neutral'
                  value={item}
                  disableIcon
                  label={item}
                  variant='plain'
                  sx={{
                    px: 1,
                    alignItems: 'center',
                  }}
                  slotProps={{
                    action: ({ checked }) => ({
                      sx: {
                        ...(checked && {
                          bgcolor: 'background.surface',
                          boxShadow: 'sm',
                          '&:hover': {
                            bgcolor: 'background.surface',
                          },
                        }),
                      },
                    }),
                  }}
                />
              ))}
            </RadioGroup>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: '5px',  flexDirection: 'column' }}>
            <Typography
              id='segmented-controls-example'
              fontWeight='lg'
              fontSize='sm'
            >
              Крепления:
            </Typography>
            <RadioGroup
              orientation='horizontal'
              aria-labelledby='segmented-controls-example'
              name='fastenings'
              value={fastenings}
              onChange={(event) => setFastenings(event.target.value)}
              sx={{
                minHeight: 30,
                padding: '4px',
                borderRadius: '12px',
                bgcolor: 'neutral.softBg',
                '--RadioGroup-gap': '4px',
                '--Radio-actionRadius': '8px',
              }}
            >
              {['Отгибные'].map((item) => (
                <Radio
                  key='Отгибные'
                  color='neutral'
                  value='Отгибные'
                  disableIcon
                  label='Отгибные'
                  variant='plain'
                  checked
                  sx={{
                    px: 1,
                    alignItems: 'center',
                  }}
                  slotProps={{
                    action: ({ checked }) => ({
                      sx: {
                        ...(checked && {
                          bgcolor: 'background.surface',
                          boxShadow: 'sm',
                          '&:hover': {
                            bgcolor: 'background.surface',
                          },
                        }),
                      },
                    }),
                  }}
                />
              ))}
            </RadioGroup>
          </Box>
          </Box>
    </>
  )
}
