import * as React from 'react';
import Box from '@mui/joy/Box';
import Card from '@mui/joy/Card';
import CardCover from '@mui/joy/CardCover';
import CardContent from '@mui/joy/CardContent';
import Typography from '@mui/material/Typography';
import tree from '../images/tree.png'

export default function Popular() {
  return (
    <Box
      style={{ margin: 40 }}
      sx={{
        perspective: '1000px',
        transition: 'transform 0.4s',
        '& > div, & > div > div': {
          transition: 'inherit',
        },
        '&:hover': {
          '& > div': {
            transform: 'rotateY(30deg)',
            '& > div:nth-child(2)': {
              transform: 'scaleY(0.9) translate3d(20px, 30px, 40px)',
            },
            '& > div:nth-child(3)': {
              transform: 'translate3d(45px, 50px, 40px)',
            },
          },
        },
      }}
    >
      <Card
        variant='outlined'
        sx={{
          minHeight: '480px',
          width: 320,
          backgroundColor: '#fff',
          borderColor: '#000',
          backgroundImage: `url(${tree})`,
          backgroundSize: 'cover'
        }}
      >
        <Typography level='h2' fontSize='lg' textColor='#000'>
          Премиальные елки(1)
        </Typography>
        <CardCover
          sx={{
            background:
              'linear-gradient(to top, rgba(0,0,0,0.4), rgba(0,0,0,0) 200px), linear-gradient(to top, rgba(0,0,0,0.8), rgba(0,0,0,0) 300px)',
            border: '1px solid',
            borderColor: '#777',
            backdropFilter: 'blur(1px)',
            backgroundImage: `url(${tree})`,
            backgroundSize: 'cover'
          }}
        >
          <Typography level='h2' fontSize='lg' textColor='#fff'>
            Лучшее качество(2)
          </Typography>
        </CardCover>
        <CardContent
          sx={{
            alignItems: 'self-end',
            justifyContent: 'flex-end',
            background:
              'linear-gradient(to top, rgba(0,0,0,0.3), rgba(0,0,0,0.3))',
            border: '1px solid',
            borderColor: '#000',
            backdropFilter: 'blur(1px)',
            backgroundImage: `url(${tree})`,
            backgroundSize: 'cover',
            borderRadius: '10px'
          }}
        >
          <Typography level='h2' fontSize='lg' textColor='#fff' m={2}>
            Твоя девушка будет мок
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
}
