      props.scrollToPopular(popularRef); // Передаем реф в функцию из пропсов, если она передана
    }
  }, [props.scrollToPopular]);
  return (
    <>
    <Box className='popular' ref={popularRef}> </Box>
      <h1 className='popular__title'>Популярные разделы</h1>
