import React from 'react';
import Select from 'react-select';

const options = [
  { value: "input1", label: "Input 1" },
  { value: "input2", label: "Input 2" },
];
const SelectPrice = () => {
  const [selectedOption, setSelectedOption] = useState(null);
  const [input1Value, setInput1Value] = useState("");
  const [input2Value, setInput2Value] = useState("");

  const handleInputChange = (e) => {
    const value = e.target.value;
    if (selectedOption && selectedOption.value === "input1") {
      setInput1Value(value);
    } else if (selectedOption && selectedOption.value === "input2") {
      setInput2Value(value);
    }
  };

  const handleSelectChange = (selected) => {
    setSelectedOption(selected);
    setInput1Value("");
    setInput2Value("");
  };

  return (
    <div>
      <Select
        options={options}
        value={selectedOption}
        onChange={handleSelectChange}
      />
      <input
        type="text"
        placeholder="Input 1"
        value={input1Value}
        onChange={handleInputChange}
        disabled={!selectedOption || selectedOption.value !== "input1"}
      />
      <input
        type="text"
        placeholder="Input 2"
        value={input2Value}
        onChange={handleInputChange}
        disabled={!selectedOption || selectedOption.value !== "input2"}
      />
    </div>
  );
}

export default SelectPrice;
