import React from 'react';
import AspectRatio from '@mui/joy/AspectRatio';
import Card from '@mui/joy/Card';
import CardContent from '@mui/joy/CardContent';
import CardOverflow from '@mui/joy/CardOverflow';
import Divider from '@mui/joy/Divider';
import Typography from '@mui/joy/Typography';
import DeleteAlert from './DeleteAlert';
import Link from '@mui/joy/Link';
import { Box } from '@mui/joy';



export default function CardBasket() {
  // const cartItems = JSON.parse(localStorage.getItem('cartItems'));
  const [cartItems, setCartItems] = useState(
    JSON.parse(localStorage.getItem('cartItems'))
  );
  const handleDelete = (card) => {
    const data = JSON.parse(localStorage.getItem('cartItems')) || [];
    const index = data.findIndex((item) => item.id === card.id);

    data.splice(index, 1);
    localStorage.setItem('cartItems', JSON.stringify(data));
    setCartItems(data);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
      {cartItems &&
        cartItems.map((card, index) => (
          <Card
            key={index}
            variant='outlined'
            sx={{
              width: '325px',
              display: 'flex',
              flexDirection: 'row',
              gap: '10px',
            }}
          >
            <img
              src={card.imageSrc}
              alt={card.title}
              style={{ width: '150px' }}
            />

            <Box display={'flex'} flexDirection={'column'}>
              <Typography variant='body1'>Цена: {card.price}</Typography>

              <Typography variant='h6'>
                <Link href='#multiple-actions' underline='none'>
                  {card.title}
                </Link>
              </Typography>
            </Box>

            <Divider orientation='vertical'>
            <DeleteAlert card={card} onClick={handleDelete} />

            </Divider>
          </Card>
        ))}
    </div>
  );
}
