import React, { useState } from 'react';
import { Box, Typography } from '@mui/joy';
import Button from '@mui/joy/Button';
import RadioModal from './RadioModal';
import Radio from '@mui/joy/Radio';
import RadioGroup from '@mui/joy/RadioGroup';
import ModalItem from './ModalItem';

export default function CardItem() {
  const [height, setHeight] = useState('');
  const [diameter, setDiameter] = useState('');
  const [fastenings, setFastenings] = useState('');

  return (
    <div
      style={{
        marginTop: '250px',
        display: 'flex',
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '30px',
        gap: '10px',
      }}
    >
      <Box sx={{ width: '250px' }}>
        <ModalItem />
      </Box>

      <Box>
        <Typography
          level='h3'
          variant='string'
          color='neutral'
          textAlign={'center'}
          marginBottom={'15px'}
        >
          Боярская елка
        </Typography>
        <Box display={'flex'} flexDirection={'column'} gap={'12px'}>
          <Box
            sx={{
              display: 'flex',
              alignItems: 'center',
              gap: '5px',
              flexDirection: 'column',
            }}
          >

          </Box>
          <Box
            sx={{
              display: 'flex',
              alignItems: 'center',
              gap: '5px',
              flexDirection: 'column',
            }}
          >
            <Typography id='segmented-control' fontWeight='lg' fontSize='sm'>
              Диаметр:
            </Typography>
            <RadioGroup
              orientation='horizontal'
              aria-labelledby='segmented-control'
              name='diameter'
              value={diameter}
              onChange={(event) => setDiameter(event.target.value)}
              sx={{
                minHeight: 30,
                padding: '4px',
                borderRadius: '12px',
                bgcolor: 'neutral.softBg',
                '--RadioGroup-gap': '4px',
                '--Radio-actionRadius': '8px',
              }}
            >
              {['135 см', '143 см', '155 см', '156 см'].map((item) => (
                <Radio
                  key={item}
                  color='neutral'
                  value={item}
                  disableIcon
                  label={item}
                  variant='plain'
                  sx={{
                    px: 1,
                    alignItems: 'center',
                  }}
                  slotProps={{
                    action: ({ checked }) => ({
                      sx: {
                        ...(checked && {
                          bgcolor: 'background.surface',
                          boxShadow: 'sm',
                          '&:hover': {
                            bgcolor: 'background.surface',
                          },
                        }),
                      },
                    }),
                  }}
                />
              ))}
            </RadioGroup>
          </Box>
          <Box
            sx={{
              display: 'flex',
              alignItems: 'center',
              gap: '5px',
              flexDirection: 'column',
            }}
          >
            <Typography
              id='segmented-controls-example'
              fontWeight='lg'
              fontSize='sm'
            >
              Крепления:
            </Typography>
            <RadioGroup
              orientation='horizontal'
              aria-labelledby='segmented-controls-example'
              name='fastenings'
              value={fastenings}
              onChange={(event) => setFastenings(event.target.value)}
              sx={{
                minHeight: 30,
                padding: '4px',
                borderRadius: '12px',
                bgcolor: 'neutral.softBg',
                '--RadioGroup-gap': '4px',
                '--Radio-actionRadius': '8px',
              }}
            >
              {['Отгибные'].map((item) => (
                <Radio
                  key='Отгибные'
                  color='neutral'
                  value='Отгибные'
                  disableIcon
                  label='Отгибные'
                  variant='plain'
                  checked
                  sx={{
                    px: 1,
                    alignItems: 'center',
                  }}
                  slotProps={{
                    action: ({ checked }) => ({
                      sx: {
                        ...(checked && {
                          bgcolor: 'background.surface',
                          boxShadow: 'sm',
                          '&:hover': {
                            bgcolor: 'background.surface',
                          },
                        }),
                      },
                    }),
                  }}
                />
              ))}
            </RadioGroup>
          </Box>
          <Button variant='outlined' style={{ margin: 'auto 25%' }}>
            В корзину
          </Button>
        </Box>
      </Box>
    </div>
  );
}
