import React from 'react';
import { FormControl } from '@mui/material';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import cardData from './Cards';

const optionsHeight = [
  { id: '1', label: '100 см' },
  { id: '2', label: '110 см' },
  { id: '3', label: '130 см' },
  { id: '4', label: '140 см' },
  { id: '5', label: '150 см' },
  { id: '6', label: '160 см' },
  { id: '7', label: '170 см' },
  { id: '8', label: '180 см' },
  { id: '9', label: '200 см' },
  { id: '10', label: '210 см' },
  { id: '11', label: '220 см' },
];

export default function SelectHeight({ selectedHeight, setSelectedHeight, setFilteredCardData }) {
  const handleHeightChange = (event) => {
    const selectedHeight = event.target.value;
    setSelectedHeight(selectedHeight);
  };

  React.useEffect(() => {
    const filteredData = cardData.filter((card) => cardData.height === selectedHeight);
    setFilteredCardData(filteredData);
  }, [selectedHeight, setFilteredCardData]);

  return (
    <FormControl sx={{ m: 1, minWidth: 120 }} size='small'>
      <InputLabel id='demo-select-small-label'>Высота</InputLabel>
      <Select
        labelId='demo-select-small-label'
        id='demo-select-small'
        value={selectedHeight}
        label='Age'
        onChange={handleHeightChange}
        sx={{ width: '120px' }}
      >
        {optionsHeight.map(({ id, label }) => (
          <MenuItem key={id} value={id}>
            {label}
          </MenuItem>
        ))}
      </Select>
    </FormControl>
  );
}
