import React from 'react';
import Card from '@mui/joy/Card';
import CardCover from '@mui/joy/CardCover';
import CardContent from '@mui/joy/CardContent';
import Typography from '@mui/joy/Typography';

import DeleteAlert from './DeleteAlert';

export default function CardSale() {
  const cartItems = JSON.parse(localStorage.getItem('cartItems'));


  return (
    <div>
      {cartItems && cartItems.map((card, index) => (
        <Card sx={{ minHeight: '280px', width: 320 }} key={index}>
          <CardCover>
            <img
              src='https://images.unsplash.com/photo-1542773998-9325f0a098d7?auto=format&fit=crop&w=320'

              loading='lazy'
              alt=''
            />
          </CardCover>
          <CardCover
            sx={{
              background:
                'linear-gradient(to top, rgba(0,0,0,0.4), rgba(0,0,0,0) 200px), linear-gradient(to top, rgba(0,0,0,0.8), rgba(0,0,0,0) 300px)',
            }}
          />
          <CardContent
            sx={{ alignItems: 'flex-start', justifyContent: 'flex-end' }}
          >
            <DeleteAlert  />
            <Typography level='title-lg' textColor='#fff'>
              {card.title}
            </Typography>
            <Typography textColor='neutral.300'>Итог: {card.price} Рублей</Typography>
          </CardContent>
        </Card>
      ))}
    </div>
  );
          }
