import React from 'react';
import {
  Checkbox,
  FormControl,
  FormControlLabel,
  FormGroup,
  FormLabel,
} from '@mui/material';
import { Box } from '@mui/joy';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';

const optionsCat = [
  { id: '1', label: 'Литые елки' },
  { id: '2', label: 'Уличные елки' },
  { id: '3', label: 'Елки с лампочками' },
  { id: '4', label: 'Заснежные елки' },
  { id: '5', label: 'Комбинированные елки' },
  { id: '6', label: 'Настольные елки' },
];

const optionsHeigth = [
  { id: '1', label: '100 см' },
  { id: '2', label: '110 см' },
  { id: '3', label: '110 см' },
  { id: '4', label: '110 см' },
  { id: '5', label: '110 см' },
  { id: '6', label: 'Настольные елки' },
  { id: '7', label: 'Настольные елки' },
  { id: '8', label: 'Настольные елки' },
  { id: '9', label: 'Настольные елки' },
  { id: '10', label: 'Настольные елки' },
  { id: '11', label: 'Настольные елки' },
];
export default function FormControlLabelPosition() {
  const [age, setAge] = React.useState('');

  const handleChange = (event) => {
    setAge(event.target.value);
  };
  return (
    <Box display={'flex'} flexDirection={'column'}>
      <FormControl component='fieldset'>
        <FormLabel component='legend'>Категории</FormLabel>
        <FormGroup aria-label='position' column='true'>
          {optionsCat.map(({ id, label }) => (
            <FormControlLabel
              key={id}
              value={id}
              control={
                <Checkbox
                  size='small'
                  style={{ marginLeft: '10px', padding: '3px' }}
                />
              }
              label={label}
              labelPlacement='end'
            />
          ))}
        </FormGroup>
      </FormControl>
      <FormControl sx={{ m: 1, minWidth: 120 }} size='small'>
        <InputLabel id='demo-select-small-label'>Высота</InputLabel>
        <Select
          labelId='demo-select-small-label'
          id='demo-select-small'
          value={age}
          label='Age'
          onChange={handleChange}
          sx={{ width: '150px' }}
        >
          <MenuItem value='lol'>
            <em>Выбрать все</em>
          </MenuItem>
          {optionsHeigth.map(({ id, label }) => (
            <MenuItem key={id} value={id}>
              {label}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
    </Box>
  );
}
