import React, { useState } from 'react';
import { Box } from '@mui/joy';
import Header from './Header';
import SelectCat from './SelectCat';
import SelectHeight from './SelectHeight';
import SelectRad from './SelectRad';
import SelectBranch from './SelectBranch';
import AspectRatio from '@mui/joy/AspectRatio';
import Button from '@mui/joy/Button';
import Card from '@mui/joy/Card';
import CardContent from '@mui/joy/CardContent';
import Typography from '@mui/joy/Typography';
import CustomIcons from './Pagination';
import Footer from './Footer';
import SelectBtn from './SelectBtn';
import { cardData } from '../constans/catalog';
import ModalItem from './ModalItem';
import CardItem from './CardItem';

export default function FormControlLabelPosition() {
  const [selectedValues, setSelectedValues] = useState({});
  const [cards, setCards] = useState(cardData);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedCardIndex, setSelectedCardIndex] = useState(null);
  const [category, setCategoty] = useState('');
  const [branch, setBranch] = useState('');
  const [height, setHeight] = useState('');
  const [radius, setRadius] = useState('');

  const handleChangeRadius = (event) => {
    setRadius(event.target.value);
    handleRadChange(event);
  };

  const handleHeigthCahnge = (event) => {
    setHeight(event.target.value);
    handleHeightChange(event);
  };

  const handleChangeBranch = (event) => {
    setBranch(event.target.value);
    handleBranchChange(event);
  };

  const handleOpenModal = (index) => {
    setSelectedCardIndex(index);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setSelectedCardIndex(null);
    setIsModalOpen(false);
  };

  const searchButton = () => {
    const filterValues = selectedValues;
    const filteredData = cardData.filter((obj) => {
      if (
        filterValues.branch &&
        filterValues.branch.length > 0 &&
        !filterValues.branch.includes(obj.branch)
      ) {
        return false;
      }

      if (
        filterValues.cat &&
        filterValues.cat.length > 0 &&
        !filterValues.cat.includes(obj.type)
      ) {
        return false;
      }

      const priceObj = obj.prices.find((price) => {
        return (
          filterValues.height &&
          price.height >= filterValues.height.min &&
          price.height <= filterValues.height.max
        );
      });

      let dimensionMatch = false; // флаг для проверки совпадения по высоте и радиусу

      for (let i = 0; i < obj.dimensions.length; i++) {
        const dimensionObj = obj.dimensions[i];
        if (
          filterValues.radius &&
          dimensionObj.radius >= filterValues.radius.min &&
          dimensionObj.radius <= filterValues.radius.max &&
          filterValues.height &&
          dimensionObj.height >= filterValues.height.min &&
          dimensionObj.height <= filterValues.height.max
        ) {
          dimensionMatch = true;
          break; // если нашли совпадение, выходим из цикла
        }
      }

      if (!dimensionMatch) {
        return false;
      }

      return true;
    });

    setCards(filteredData);
  };

  const handleAddToCart = (card) => {
    const { imageSrc, price, title, height, radius, branch } = card;
    const cartItems = JSON.parse(localStorage.getItem('cartItems')) || [];
    const newItem = { imageSrc, price, title, height, radius, branch };
    cartItems.push(newItem);
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
  };

  const handleReset = () => {
    setCategoty('');
    setBranch('');
    setHeight('');
    setRadius('');
    setSelectedValues({});
    setCards(cardData);
  };

  const handleChangeCat = (event) => {
    setCategoty(event.target.value);
    handleCatChange(event);
  };

  const handleCatChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      cat: event.target.value,
    }));
  };

  const handleHeightChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      height: event.target.value,
    }));
  };

  const handleRadChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      radius: event.target.value,
    }));
  };

  const handleBranchChange = (event) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      branch: event.target.value,
    }));
  };

  return (
    <Box
      display={'flex'}
      flexDirection={'column'}
      alignItems={'center'}
      marginBottom={'30px'}
    >
      <Header />

      <Box
        display={'flex'}
        flexDirection={'row'}
        marginTop={'100px'}
        gap={'30px'}
        alignItems={'center'}
        flexWrap={'wrap'}
      >
        <SelectCat
          handleCatChange={handleCatChange}
          category={category}
          changeCategory={handleChangeCat}
        />
        <SelectHeight
          handleHeightChange={handleHeightChange}
          height={height}
          changeHeight={handleHeigthCahnge}
        />
        <SelectRad
          handleRadChange={handleRadChange}
          changeRad={handleChangeRadius}
          radius={radius}
        />
        <SelectBranch
          handleBranchChange={handleBranchChange}
          branch={branch}
          changeBranch={handleChangeBranch}
        />
        <SelectBtn onReset={handleReset} onSearch={searchButton} />
      </Box>
      <Box
        display={'flex'}
        flexWrap={'wrap'}
        gap={'30px'}
        justifyContent={'center'}
        marginTop={'30px'}
      >
        {cards.map((card, index) => (
          <Card key={index} sx={{ width: 200 }}>
            <div>
              <Typography
                sx={{ cursor: 'pointer' }}
                onClick={() => handleOpenModal(index)}
                level='title-lg'
              >
                {card.title}
              </Typography>
            </div>
            <AspectRatio minHeight='320px' maxHeight='200px'>
              <ModalItem images={card.imageSrc.map((image) => image.image)} />
            </AspectRatio>
            <CardContent orientation='horizontal'>
              <div>
                <Typography level='body-xs'>От:</Typography>
                <Typography fontSize='sm' fontWeight='lg'>
                  {Math.min(...card.prices.map((price) => price.price))} ₽
                </Typography>
              </div>
              <Button
                variant='solid'
                size='md'
                color='primary'
                aria-label={`Explore ${card.title}`}
                sx={{ ml: 'auto', alignSelf: 'center', fontWeight: 600 }}
                onClick={() => handleAddToCart(card)}
              >
                Размеры
              </Button>
            </CardContent>
          </Card>
        ))}
        {selectedCardIndex !== null && (
          <CardItem
            card={cards[selectedCardIndex]}
            isOpen={handleOpenModal}
            onClose={handleCloseModal}
            addBasket={handleAddToCart}
          />
        )}
      </Box>
      <CustomIcons sx={{ marginTop: '30px' }} />
      <Footer />
    </Box>
  );
}
