import React, { useState } from 'react';
import FormSell from './ModalSell';
import { Box } from '@mui/joy';
import CardSale from './CardSale';
import Typography from 'antd/es/typography/Typography';
import CloseIcon from '@mui/icons-material/Close';

export default function ModalSale({ setIsModalOpen }) {
  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const [showOverlay, setShowOverlay] = useState(true);

  const handleOverlayClick = () => {
    setIsModalOpen(false);
  };

  const cardSaleData = JSON.parse(localStorage.getItem('cartItems'))

  return (
    <>
      {showOverlay && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            zIndex: 2,
          }}
          onClick={handleOverlayClick}
        />
      )}

      {cardSaleData ? (
        <Box
          display={'flex'}
          flexDirection={'column'}
          textAlign={'center'}
          backgroundColor={'white'}
          borderRadius={'10px'}
          padding={'15px'}
          gap={'10px'}
          position={'absolute'}
          top={'70%'}
          left={'33%'}
          zIndex={'3'}
        >
          <CloseIcon
            sx={{
              cursor: 'pointer',
              position: 'absolute',
              top: '-35px',
              right: '-30px',
              fontSize: '40px',
              color: 'white',
            }}
            onClick={handleCloseModal}
          />
          <Typography style={{ margin: '0px' }} component='h2'>
            Ваш Заказ
          </Typography>
          <Box display={'flex'} gap={'20px'} flexDirection={'row'}>
            <CardSale cardSaleData={cardSaleData} />
            <FormSell />
          </Box>
        </Box>
      ) : (
        handleCloseModal()
      )}
    </>
  );
}
