import React from 'react';
import AspectRatio from '@mui/joy/AspectRatio';
import Card from '@mui/joy/Card';
import CardContent from '@mui/joy/CardContent';
import CardOverflow from '@mui/joy/CardOverflow';
import Divider from '@mui/joy/Divider';
import Typography from '@mui/joy/Typography';

import Link from '@mui/joy/Link';

export default function CardBasket(props) {
  const { cartItems, setCartItems } = props;

  const handleAddToCart = (product) => {
    const index = cartItems.findIndex((item) => item.id === product.id);
    if (index !== -1) {
      const updatedCartItems = [...cartItems];
      updatedCartItems[index].quantity += 1;
      updatedCartItems[index].totalPrice += product.price;
      setCartItems(updatedCartItems);
    } else {
      const newCartItem = {
        id: product.id,
        name: product.name,
        price: product.price,
        imageSrc: product.imageSrc,
        title: product.title,
        quantity: 1,
        totalPrice: product.price,
      };
      setCartItems([...cartItems, newCartItem]);
    }
  };

  return (
    <div>
      {/* Отображаем каждый товар в корзине */}
      {cartItems.map((product) => (
        <Card key={product} variant='outlined' sx={{ maxWidth: 220 }}>
          <CardOverflow>
            <AspectRatio ratio='2'>
              <img src={product.imageSrc} alt={product.title} />
            </AspectRatio>
          </CardOverflow>
          <CardContent>
            <Typography variant='h6'>
              <Link href='#multiple-actions' underline='none'>
                {product.title}
              </Link>
            </Typography>
            <Typography variant='body1'>
              <Link href='#multiple-actions'>{product.price}</Link>
            </Typography>
          </CardContent>
          <CardOverflow variant='soft'>
            <Divider inset='context' />
            <CardContent orientation='horizontal'>
              <Typography variant='body2'>1 шт</Typography>
              <Divider orientation='vertical' />
              <Typography variant='body2'>Итог: {product.price}</Typography>
            </CardContent>
          </CardOverflow>
        </Card>
      ))}
    </div>
  );
}
