import React, { useState } from 'react';
import Radio from '@mui/joy/Radio';
import RadioGroup from '@mui/joy/RadioGroup';
import { Box, Typography } from '@mui/joy';

export default function RadioModal({ card, onPriceChange }) {
  const [values, setValues] = useState({
    height: card.prices[0].height + ' см',
    diameter: card.dimensions[0].radius + ' см',
  });



 const getPrice = (values) => {
  const { height } = values;
  const priceObj = card.prices.find((price) => price.height === height);
  const dimensionObj = card.dimensions.find((dimension) => dimension.height === height.replace('см', '').trim());
  if (priceObj && dimensionObj) {
    const price = priceObj.price;
    const diameterPrice = dimensionObj.price || 0;
    const totalPrice = price + diameterPrice;

    return totalPrice;

  }
  return null;
};

const handleChange = (groupId, value) => {
  setValues((prevValues) => {
    const newValues = { ...prevValues, [groupId]: value };
    if (groupId === 'height') {
      const dimensionObj = card.dimensions.find(
        (dimension) => dimension.height === value.replace(' см', '')
      );
      if (dimensionObj) {
        newValues.diameter = dimensionObj.radius + ' см';
      }
    }
    const newPrice = getPrice(newValues);
    onPriceChange(newPrice); // добавлено обновление цены

    return newValues;
  });
};

  const radioGroups = [
    {
      id: 'height',
      label: 'Высота:',
      options: card.prices.map((price) => price.height + ' см'),
    },
    {
      id: 'diameter',
      label: 'Диаметр:',
      options: card.dimensions.map(
        (dimension) => dimension.radius + ' см'
      ),
    },
    {
      id: 'fastenings',
      label: 'Крепления:',
      options: [card.branch],
      defaultValue: card.branch,
    },
  ];

  return (
    <Box
      display='flex'
      flexDirection='column'
      gap={2}
      textAlign={'center'}
      alignItems={'center'}
    >
      {radioGroups.map(({ id, label, options, defaultValue }) => (
        <Box
          key={id}
          sx={{
            display: 'flex',
            flexDirection: 'column',
            width: 'fit-content',
          }}
        >
          <Typography fontWeight='lg' fontSize='sm'>
            {label}
          </Typography>
          <RadioGroup
            orientation='horizontal'
            aria-labelledby={id}
            name={id}
            value={values[id] || defaultValue || ''}
            onChange={(event) => handleChange(id, event.target.value)}
            sx={{
              minHeight: 30,
              padding: '4px',
              borderRadius: '12px',
              bgcolor: 'neutral.softBg',
              '--RadioGroup-gap': '4px',
              '--Radio-actionRadius': '8px',
            }}
          >
            {options.map((option) => (
              <Radio
                key={option}
                color='neutral'
                value={option}
                disableIcon
                label={option}

                variant='plain'
                sx={{
                  px: 1,
                  alignItems: 'center',
                }}
                slotProps={{
                  action: ({ checked }) => ({
                    sx: {
                      ...(checked && {
                        bgcolor: 'background.surface',
                        boxShadow: 'sm',
                        '&:hover': {
                          bgcolor: 'background.surface',
                        },
                      }),
                    },
                  }),
                }}
              />
            ))}
          </RadioGroup>
        </Box>
      ))}
    </Box>
  );
}
