import { Button, Form, Input } from 'antd';
import React, { useState, useRef } from 'react';
import emailjs from '@emailjs/browser';

const FormSell = () => {
  const [form] = Form.useForm();
  const [requiredMark, setRequiredMarkType] = useState('optional');

  const formEmail = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        'service_daaqfom',
        'template_97dh8tr',
        form.current,
        'b4obcp2X-FryQP0cP'
      )
      .then(
        (result) => {
          console.log(result.text);
          console.log('OK')
        },
        (error) => {
          console.log(error.text);
          console.log('Error')
        }
      );
  };

  const onRequiredTypeChange = ({ requiredMarkValue }) => {
    setRequiredMarkType(requiredMarkValue);
  };
  return (
    <Form
      form={formEmail}
      layout='vertical'
      initialValues={{
        requiredMarkValue: requiredMark,
      }}
      onValuesChange={onRequiredTypeChange}
    >
      <Form.Item label='Имя' required ref={form} onSubmit={sendEmail}>
        <Input placeholder='Ваше Имя' data-name='user_name' />
      </Form.Item>
      <Form.Item label='Фамилия'>
        <Input placeholder='Ваша Фамилия' name='user_email' />
      </Form.Item>
      <Form.Item label='Номер телефона'>
        <Input placeholder='Номер телефона' required name='message' />
      </Form.Item>
      <Form.Item label='Адрес доставки'>
        <Input placeholder='Адрес' />
      </Form.Item>
      <Form.Item label='Комментарии к заказу'>
        <Input
          style={{ width: '350px' }}
          placeholder='Оставьте Ваши пожелания к заказу'
        />
      </Form.Item>
      <Form.Item>
        <Button
          type='primary'
          style={{ width: '350px', height: '50px' }}
          htmlType='submit'
        >
          Оформить заказ
        </Button>
      </Form.Item>
    </Form>
  );
};
export default FormSell;
