import React, { useState, useEffect } from 'react';
import FormSell from './ModalSell';
import { Box } from '@mui/joy';
import CardSale from './CardSale';
import Typography from 'antd/es/typography/Typography';
import CloseIcon from '@mui/icons-material/Close';
import { useTheme } from '@mui/material/styles';
import useMediaQuery from '@mui/material/useMediaQuery';

export default function ModalSale({ setIsModalOpen }) {
  const [cartItems, setCartItems] = useState(
    JSON.parse(localStorage.getItem('cartItems'))
  );
  const [isEmpty, setIsEmpty] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  useEffect(() => {
    if (isEmpty) {
      setIsModalOpen(false);
    }
  }, [isEmpty, setIsModalOpen]);

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const [showOverlay, setShowOverlay] = useState(true);

  const handleOverlayClick = () => {
    setIsModalOpen(false);
  };

  return (
    <>
      {/* {showOverlay && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            zIndex: 2,
          }}
          onClick={handleOverlayClick}
        />
      )} */}
      <Box
        display={'flex'}
        flexDirection={'column'}
        textAlign={'center'}
        backgroundColor={'white'}
        borderRadius={'8px'}
        padding={'15px'}
        gap={'10px'}
      >
        <div
          style={{
            backgroundColor: 'white',
            padding: '20px',
            borderRadius: '30px',
            gap: '10px',
            position: 'fixed',
            zIndex: 2,
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
          }}
          className='sale_modal'
        >
          <CloseIcon
            sx={{
              cursor: 'pointer',
              position: 'absolute',
              top: '-35px',
              right: '-30px',
              fontSize: '40px',
              color: 'white',
            }}
            onClick={handleCloseModal}
          />
          {/* <Typography style={{ margin: '0px' }} component='h2'>
            Ваш Заказ
          </Typography>
          <Box display={'flex'} gap={'20px'} flexDirection={'row'}>
            <CardSale setIsEmpty={setIsEmpty} />
            <FormSell />
          </Box> */}
        </div>
      </Box>
    </>
  );
}
